package ec.edu.utpl.computacion.pa.examen.parciall;import javax.persistence.EntityManager;import javax.persistence.EntityTransaction;public class RegistroVotos {    private EntityManager em;    public RegistroVotos(EntityManager em) {        this.em = em;    }    public synchronized void registrarVoto(Asambleista asambleista, String opcion) {        EntityTransaction transaction = em.getTransaction();        transaction.begin();        try {            Voto voto = new Voto();            voto.setAsambleista(asambleista);            voto.setOpcion(opcion);            em.persist(voto);            transaction.commit();        } catch (Exception e) {            transaction.rollback();            e.printStackTrace();        }    }}