package ec.edu.utpl.computacion.pa.examen.parciall;import java.util.Random;public class VotacionThread implements Runnable {    private Asambleista asambleista;    private RegistroVotos registroVotos;    public VotacionThread(Asambleista asambleista, RegistroVotos registroVotos) {        this.asambleista = asambleista;        this.registroVotos = registroVotos;    }    @Override    public void run() {        try {            Thread.sleep(1000);        } catch (InterruptedException e) {            throw new RuntimeException(e);        }        Random random = new Random();        int voto = random.nextInt(100) + 1;        String opcion;        if (voto <= 25) {            opcion = "si";        } else if (voto <= 50) {            opcion = "no";        } else {            opcion = "abstencion";        }        registroVotos.registrarVoto(asambleista, opcion);    }}